    var loadProgramsForCampus = function () {

        var name = $("#ddlbCampus option:selected").text();
        var val = $("#ddlbCampus").val();
        $hCampus.val(val);
        $hCampusName.val(name);
        
        $("#tbProgram").val("");
        
        if (val !== "") {

            $("#tbProgram").removeAttr("disabled");

            arrProgramsForCampus = new Array();

            oCampusProgramsTable.fnClearTable();

            var arrLen = arrAllPrograms.length;
            for (var i = 0; i < arrLen; i++) {
                for (var y = 0; y < arrAllPrograms[i].Campuses.length; y++) {
                    if (val == arrAllPrograms[i].Campuses[y].CampusCode) {                        
                        arrProgramsForCampus.push({ Code: arrAllPrograms[i].ProgramCode, Desc: arrAllPrograms[i].ProgramDescr });
                        continue;
                    }
                }
            }

            programsForCampusCount = arrProgramsForCampus.length;
            for (var x = 0; x < programsForCampusCount; x++) {
                var href = "<a href=\"" + arrProgramsForCampus[x].Code + "\">" + arrProgramsForCampus[x].Desc + "</a>";
                 oCampusProgramsTable.fnAddData([href]);
            }
                       
            var oSettings = oCampusProgramsTable.fnSettings();
            oSettings.iDisplayStart = 0;
            oCampusProgramsTable.fnDraw();

            $("#hlShowCampusPrograms").html(campusLightBoxLink.replace("{0}", $("#ddlbCampus option:selected").text()));
            //$("#hlShowCampusPrograms").data("campusId", $("#ddlbCampus").val());
            $("#hlShowCampusPrograms").css("display", "inline");
            $("#divCampusPrograms").dialog("option", "title", $("#ddlbCampus option:selected").text() + " Programs");

            $("#tbProgram").flushCache();
            $("#tbProgram").autocomplete(arrProgramsForCampus,
                {
                    formatItem: function(row, pos, totalItems, searchTerm) {
                        if (jQuery.isArray(row)) {
                            return row[0]; // 'No Records'
                        } else {
                            return row.Desc;
                        }
                    },
                    formatResult: function (row, pos, totalItems) {
                        return row.Desc;
                    }
                }).result(function(event, item) {
                    //console.log(item);
                    $hProgram.val(item.Code);
                    $hProgramName.val(item.Desc);
                });

        } else {
                        
            $("#tbProgram").attr("disabled", "disabled");
            $("#hlShowCampusPrograms").css("display", "none");
        }
    };

