    var calculateTotals = function () {
        
        var income = 0;
        var expense = 0;
        var financial = 0;

        $("#tblMain .incometotal").each(function (index) {
            
            var val = $(this).val();
            if (val != "0" && !isNaN(val)) {
                income += parseFloat(val);
            }            
        });
        $totalIncome.text(income);
        $hTotalIncome.val(income);

        $("#tblMain .nomultiply, #tblMain .multiplyresult").each(function (index) {

            var val = $(this).val();
            if (val != "0" && !isNaN(val)) {
                expense += parseFloat(val);
            }
        });
        $totalExpenses.text(expense);
        $hTotalExpenses.val(expense);

        financial = (income - expense);
        $financialNeed.text(financial);
        $hFinancialNeed.val(financial);
    };

