    var addCampusValidator = function(eligibleCampuses) {

        //console.log(eligibleCampuses);
        
        if (eligibleCampuses.length === 0) { disableForm(); return; }
        
        jQuery.validator.addMethod("validCampus", function (value, element) {
            var result = false;
            var val = value;
            //console.log("ELEM:" + val);

            if ($("#ddlbCampus").val() !== ""
                && jQuery.inArray($("#ddlbCampus").val(), eligibleCampuses) !== -1)
            {
                result = true;
            }

            //console.log(result);
            return result;
        });        
    };

